var nconf = require('nconf');
var path = require('path');

exports.loadConfigs = function () {
    var environment = process.env.NODE_ENV || nconf.get('environment') || "dev";
    console.log('=environment>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>', environment);
    nconf.use('file', {file: path.join(__dirname, '../config/' + environment + '.json')});
};